import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { uiSliceActions } from "./store/ui-slice";
import Notification from "./components/UI/Notification";

const isInitial= true;

function App() {
  const isCartVisible = useSelector((state) => state.uiReducer.isCartVisible);
  const cart = useSelector((state) => state.cartReducer);
  const notification = useSelector((state) => state.uiReducer.notification);
  const dipatchFn = useDispatch();

  useEffect(() => {

    if(isInitial){
      isInitial = false;
      return;
    }

    const sendCartInfo = async () => {
      try {
        dipatchFn(
          uiSliceActions.showNotification({
            status: "pending",
            title: "Sending...",
            message: "Sending cart data!",
          })
        );
        const response = await fetch(
          "https://react-http-55fae-default-rtdb.firebaseio.com/cart.json",
          {
            method: "PUT",
            body: JSON.stringify(cart),
          }
        );
        if (!response.ok) {
          throw new Error("Error sending data!");
        }

        // const data = await response.json();
        dipatchFn(
          uiSliceActions.showNotification({
            status: "success",
            title: "Successful",
            message: "Succesfully Sent Data!",
          })
        );
      } catch (error) {
        dipatchFn(
          uiSliceActions.showNotification({
            status: "error",
            title: "Error occurred",
            message: "Error occurred in sending the data!",
          })
        );
      }
    };

    sendCartInfo();
  }, [cart, dipatchFn]);

  return (
    <>
      {notification && <Notification status={notification.status} message={notification.message} title={notification.title} />}
      <Layout>
        {isCartVisible && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
