import { createSlice } from "@reduxjs/toolkit";
import { uiSliceActions } from "./ui-slice";

const initialState = {
  items: [],
  totalQuantity: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addItemstoCart(state, action) {
      const newItem = action.payload;
      const itemExistsinCart = state.items.find(
        (item) => item.id === newItem.id
      );
      state.totalQuantity++;
      if (itemExistsinCart) {
        itemExistsinCart.quantity++;
        itemExistsinCart.totalPrice += newItem.price;
      } else {
        state.items.push({
          id: newItem.id,
          name: newItem.title,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
        });
      }
    },
    removeItemsfromCart(state, action) {
      const id = action.payload;
      const itemExistsinCart = state.items.find((item) => item.id === id);
      state.totalQuantity--;
      if (itemExistsinCart.quantity === 1) {
        state.items = state.items.filter((item) => item.id !== id);
      } else {
        itemExistsinCart.quantity--;
        itemExistsinCart.totalPrice -= itemExistsinCart.price;
      }
    },
  },
});

const sendCartData = (cart) => {
  return async (dispatch) => {
    const sendData = async () => {
      const response = await fetch(
        "https://react-http-55fae-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );
      if (!response.ok) {
        throw new Error("Error sending data!");
      }
    };
    try {
      dipatchFn(
        uiSliceActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );
      sendData();

      dipatchFn(
        uiSliceActions.showNotification({
          status: "success",
          title: "Successful",
          message: "Succesfully Sent Data!",
        })
      );
    } catch (error) {
      dipatchFn(
        uiSliceActions.showNotification({
          status: "error",
          title: "Error occurred",
          message: "Error occurred in sending the data!",
        })
      );
    }
  };
};

export const cartActions = cartSlice.actions;
export default cartSlice.reducer;
